mapper.select:
select*from (
select rownum as rn, A.* from(
select
faq_no,faq_title,faq_head,faq_content,wdate,member_id
from FAQ
where ${type} like '%'||#{keyword}||'%'
order by faq_no desc, wdate desc
) A
) where rn between #{start} and #{finish}

		select*from (
		select rownum as rn, A.* from(
		select*

		from FAQ
		order by faq_no desc, wdate desc
		) A
		) where rn between #{start} and #{finish}

mapper.update:
update FAQ set
faq_title = #{faq_title},
faq_head = #{faq_head},
faq_content = #{faq_content}
where faq_no= #{faq_no}
